/* ACTIONS */
import Utils from "./Utils";

const userConstants = {
    LOGIN: 'USER_LOGIN',
    LOGOUT: 'USER_LOGOUT',
};
/* ACTION GENERATORS */
export const userActions = {
    login,
    logout };
function login(user) {
    Utils.saveUser(user)
    return { type: userConstants.LOGIN, user }
}
function logout() {
    Utils.removeUser()
    return {type: userConstants.LOGOUT}
}
